import Calc from './Component/Calc';
import './App.css';
import { useState } from 'react';

function App() {

  const [display, setDisplay] = useState(0)

  let calcString = ""

  const handleClick = (event) => {
    event.preventDefault()
    setDisplay (event.target.value)
    currentCalc = calcString + event.target.value

  }

  const handleSubmit = (event) =>{
    setDisplay(eval(currentCalc))
}


const resetCalc = () =>{
  setDisplay(0)
  currentCalc=""
}
  return (
    <div id="calculator-container">
        <section id="result"><h1>0</h1></section>
        <section id="buttons-container">
            <div className="calc-button tertiary-btn">AC</div>
            <div className="calc-button tertiary-btn"><span className="material-symbols-rounded size-48">backspace</span></div>
            <div className="calc-button tertiary-btn">"% onClick={handleClick}</div>
            <div className="calc-button primary-btn">"/" onClick={handleClick}</div>
            <div className="calc-button primary-container-btn" value="7">7</div>
            <div className="calc-button primary-container-btn" value="8">8</div>
            <div className="calc-button primary-container-btn" value="9">9</div>
            <div className="calc-button primary-btn">"*"</div>
            <div className="calc-button primary-container-btn" value="4">4</div>
            <div className="calc-button primary-container-btn" value="5">5</div>
            <div className="calc-button primary-container-btn" value="6">6</div>
            <div className="calc-button primary-btn">"-"</div>
            <div className="calc-button primary-container-btn" value="1">1</div>
            <div className="calc-button primary-container-btn" value="2">2</div>
            <div className="calc-button primary-container-btn" value="3">3</div>
            <div className="calc-button primary-btn">"+"</div>
        </section>
        <section id="bottom-btns">
            <div className="calc-button primary-container-btn" id="zero-btn" value="0">0</div>
            <div className="calc-button primary-container-btn" value=".">&period;</div>
            <div className="calc-button primary-btn">&equals;</div>
        </section>
     </div>



  );
}

export default App;
