import Calc from './Component/Calc';
import './App.css';
import { useState } from 'react';

function App() {


  const [display, setDisplay] = useState(0);

  let currentCalc = '';

  const handleClick = (event) => {
      event.preventDefault();
      setDisplay(event.target.value);
      currentCalc = currentCalc + event.target.value;
      console.log(currentCalc);
  }

  const handleSubmit = () => {
      setDisplay(eval(currentCalc));
  }

  const resetCalc = () =>{
      setDisplay(0);
      currentCalc="";
  }

  return (
      <div id="calcContainer">
          <section id="result"><h1>0</h1></section>
          <section id="buttons-container">
              <button className="calc-button tertiary-btn" onClick={resetCalc}>AC</button>
              <button className="calc-button tertiary-btn"><span className="material-symbols-rounded size-48">backspace</span></button>
              <button className="calc-button tertiary-btn" value="%" onClick={handleClick}>&percnt;</button>
              <button className="calc-button primary-btn" value="/" onClick={handleClick}>&div;</button>
              <button className="calc-button primary-container-btn" value="7" onClick={handleClick}>7</button>
              <button className="calc-button primary-container-btn" value="8" onClick={handleClick}>8</button>
              <button className="calc-button primary-container-btn" value="9" onClick={handleClick}>9</button>
              <button className="calc-button primary-btn" value="*" onClick={handleClick}>&times;</button>
              <button className="calc-button primary-container-btn" value="4" onClick={handleClick}>4</button>
              <button className="calc-button primary-container-btn" value="5" onClick={handleClick}>5</button>
              <button className="calc-button primary-container-btn" value="6" onClick={handleClick}>6</button>
              <button className="calc-button primary-btn" value="-" onClick={handleClick}>&minus;</button>
              <button className="calc-button primary-container-btn" value="1" onClick={handleClick}>1</button>
              <button className="calc-button primary-container-btn" value="2" onClick={handleClick}>2</button>
              <button className="calc-button primary-container-btn" value="3" onClick={handleClick}>3</button>
              <button className="calc-button primary-btn" value="+" onClick={handleClick}>&plus;</button>
          </section>
          <section id="bottom-btns">
              <div className="calc-button primary-container-btn" id="zero-btn" value="0" onClick={handleClick}>0</div>
              <div className="calc-button primary-container-btn" value="." onClick={handleClick}>&period;</div>
              <div className="calc-button primary-btn" value="=" onClick={handleSubmit}>&equals;</div>
          </section>
      </div>
  )
}

export default App;
